<?php


    class RMap{

        private static function getMap(){
            $str = file_get_contents('../map.json');
            return json_decode($str,true);

        }

        public static function insertMap($smarty){
            $map = self::getMap();
//            var_dump($map);
            $smarty->assign('rMap',$map );
        }

    }



