'use strict';
const lang = exports;

//语言增强

lang.mapObject = (obj, func) => {
    let result = {};
    for (let key of Object.keys(obj)) {
        result[key] = func.call(obj, key, obj[key]);
    }
    return result;
};

lang.mapSet = (s, func) => {
    let result = [];
    for (let key of s) {
        result.push(func.call(s, key));
    }
    return result;
};

lang.set2array = setInstance => {
    return Array.from(setInstance);
};

lang.hasProp = (ctx, key)=> {
    return {}.hasOwnProperty.call(ctx, key);
};
