'use strict';

const os = require('os');
const osType = os.platform();
const osTable = {
  win32: 0,
  freebsd: 1,
  linux: 2,
  sunos: 3,
  darwin: 4,
};

const currentOs = osTable[osType];
const partedSymbol = ['\\'][currentOs] || '/';

module.exports = {
  partedSymbol,
  os: ['windows', 'freebsd', 'linux', 'solaries', 'osx'][currentOs],
  partedRegexSymbol: `\\${partedSymbol}`,
  currentDir: `.${partedSymbol}`,
  parentDir: `..${partedSymbol}`,
  EOL: os.EOL,
  usualLogDir: [[os.homedir(), 'pss', 'log'].join(partedSymbol)][currentOs] || '/var/log/pss',
  mkdirCommand: ['md '][currentOs] || 'mkdir -p ',
  cpCommand: ['xcopy /s /E /y /c '][currentOs] || '\\cp -fr ',
  delCommad: [['del /s /Q ', 'rd /s /Q ']][currentOs] || ['rm -fr '],
};

