'use strict';
const should = require('chai').should;
const Processor = require('../lib/file-processor');

describe('File Processor ', function () {

    describe('#require built-in module should return its own', function () {
        let fileProcessor = new Processor();
        before(function(){
            fileProcessor.type = 'css';
        });
        it('should be css module while require css', function () {
            fileProcessor.processor('name').should.equal('css');
        });
    });
    describe('#require undefined module should return its default', function () {
        let fileProcessor = new Processor();

        before(function(){
            fileProcessor.type = 'doc';
        });
        it('should be default module while require doc', function () {
            fileProcessor.processor('name').should.equal('default');
        });
    });
});