'use strict';
const selfOs = require('../lib/os'),
    should = require('chai').should(),
    systemOs = require('os');

describe('custom os module', function(){
    describe('#os', function(){
        it('should return right os type', function(){
            //selfOs.os.should.equal('windows');
        });
        it('should return right os part symbol', function(){
            let symbol = (()=>{
                if(systemOs.platform() === 'win32'){
                    return '\\';
                }
                else {
                    return '/';
                }
            })();
            selfOs.partedSymbol.should.equal(symbol)
        });
    });
});